import React,{useState} from 'react'
import { Box, Button,TextField, Typography } from '@mui/material'
import validator from 'validator';

const Contacts = () => {

  const [emailError, setEmailError] = useState('')
  const validateEmail = (e) => {
    var email = e.target.value
  
    if (!validator.isEmail(email)) {
      setEmailError('Enter valid Email!')
    } else {
      setEmailError(null)
    }
  }
  
  return (

    <Box style={{textAlign:"center"}}>
<Typography
        variant="h3"
        sx={{ fontSize: "30px", fontWeight: "bold", color: "#000339", my: 2, marginBottom:"40px" }}
      >
        Feel Free To Contact
      </Typography>
  <form 
  action="https://formspree.io/f/xbjbkqqn"
  method="POST" name='emle' style={{ marginLeft:"450px", marginRight:"450px", paddingTop:"40px", paddingBottom:"40px",boxShadow:" 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)", marginBottom:"50px", borderRadius:"10px"}} >
<Box style={{justifyContent:"center"}}>
<TextField id="outlined-basic" label="Name" name='Name' variant='outlined' style={{textAlign:"center", width:"380px"}}/>
</Box>
<Box>
<TextField id="outlined-basic" label="Email" name='Email' variant='outlined' required style={{textAlign:"center", width:"380px", marginTop:"10px"}} onChange={(e) => validateEmail(e)}/> <br/>

<span style={{color: 'red', fontSize:"10px", marginLeft:"50px", display:"flex"}}>{emailError}</span>

</Box>
<Box>
<TextField id="outlined-multiline-flexible" label="Message" name='' multiline maxRows={4} style={{width:"380px", marginTop:"10px"}} /></Box>
<Box>
<Button variant='contained'type='submit' value='send' style={{marginTop:"10px",width:"380px"}}>SUBMIT</Button>
</Box>
</form>
</Box>
  );
}

export default Contacts
