import React from "react";
import { useEffect } from "react";
import { useContext , useState} from "react";

//context api
//context hooks

//context(warehouse)   // ye data ghr
//Provider             //  ye phuchane wala 
//Consumer   (useContext())   // ye consumer

const API_URL = `http://omdbapi.com/?apikey=b864d43e&s=titanic`

const AppContext = React.createContext();

//we need to create a provider function
const AppProvider = ({children}) => {

    const [isLoading, setisLoading] = useState(true);
    const [movie, setMovie] = useState([]);
    const[isError, setIsError] = useState({ show: "false", msg: ""});

    const getMovies = async (url) => {
        try{
            const res = await fetch(url);
            const data = await res.json();
            console.log(data);
            if (data.Response === "True") {
                setisLoading(false);
                setMovie(data.search);
            } else {
                setIsError({
                    show: true,
                    msg: data.error,
                });
            }
        } catch(error) {
            console.log(error);
        }
    };

    useEffect(() => {
        getMovies(API_URL);
    }, []);

    return <AppContext.Provider value={{ isLoading}}>{children}</AppContext.Provider>
};

// here instead of saurabh u can use objects also like api
 
 const useGlobalContext = () => {
    return useContext(AppContext);
 }
// global custom hooks

export default AppProvider ;
export {AppContext, useGlobalContext};
