import React ,{useState} from 'react'
import axios from "axios"
im

const FetchNews = () => {

    const [news, setNews] = useState([])   //here in news var all the data will be save
                                           
    

    axios.get("https://newsapi.org/v2/top-headlines?country=in&apiKey=bdd789c28b3a4c80bece26cd694884b5")

    .then((response)=>{
        console.log(response); // it is just used for print data in console
        setNews(response.data.articles) //and here all the data will store in news variable

    })

  return (
    <>
    <div className='container my-3'>
        <div className='row justify-content-center'>
            <div className='col-4 '>
                <h1 >Top News of India</h1>
            </div>
        </div>
    </div>

    <div className='container'>
        <div className='row'>
            {
                news.map((value) => {
                    return(
                        <div className='col-4'>
                        <div className="card mx-3 my-3" style={{width:"18rem"}}>
                        <img src={value.urlToImage} className="card-img-top" alt="..."/>
                        <div className="card-body">
                        <h5 className='card-title'>{value.title}</h5>
                        <p className='card-description'>{value.description}</p>
                    </div>
                </div> 
            </div>
        
         );
    })
 }
            
            </div>
        </div>
    </>
  )
}

export default FetchNews
