import React,{useState} from 'react'
import { Box, Button,TextField, Typography,styled } from '@mui/material'
import validator from 'validator'


const Contacts = () => {

  const ContBox = styled(Box)(({ theme }) => ({
    textAlign:"center",
    justifyContent:"space-eve",
    gap: theme.spacing(5),
    [theme.breakpoints.down("md")]: {
      flexDirection: "column",
      alignItems: "center",
      textAlign: "center",
    },
  }));

  const [emailError, setEmailError] = useState('')
  const validateEmail = (e) => {
    var email = e.target.value
  
    if (!validator.isEmail(email)) {
      setEmailError('Enter valid Email!')
    } else {
      setEmailError(null)
    }
  }
  
  return (

    <ContBox id="section5">
    <Typography
        variant="h3"
        sx={{ fontSize: "30px", fontWeight: "bold", color: "#000339", my: 2, marginBottom:"40px", marginTop:"40px" }}
      >
        Feel Free To Contact
      </Typography>
      <Box style={{justifyContent:"space-evenly", textAlign:"center"}}>
  <form 
  action="https://formspree.io/f/xbjbkqqn"
  method="POST" name='emle' style={{ justifyContent:"center",maxWidth:"400px", paddingTop:"20px", paddingBottom:"40px",boxShadow:" 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)", marginBottom:"50px", borderRadius:"10px"}} >
    <Typography
        variant="h3"
        sx={{ fontSize: "20px", fontWeight: "bold", color: "#000339",marginBottom:"15px" }}
      >
        Any Message? Write Here...
      </Typography>
<Box style={{justifyContent:"center"}}>
<TextField id="outlined-basic" label="Name" name='Name' variant='outlined' style={{textAlign:"center", width:"380px"}}/>
</Box>
<Box>
<TextField id="outlined-basic" label="Email" name='Email' variant='outlined' required style={{textAlign:"center", width:"380px", marginTop:"10px"}} onChange={(e) => validateEmail(e)}/> <br/>

<span style={{color: 'red', fontSize:"10px", marginLeft:"50px", display:"flex"}}>{emailError}</span>

</Box>
<Box>
<TextField id="outlined-multiline-flexible" label="Message" name='Message' multiline maxRows={4} style={{width:"380px", marginTop:"10px"}} /></Box>
<Box>
<Button variant='contained'type='submit' value='send' style={{marginTop:"10px",width:"380px"}}>Message me</Button>
</Box>
</form>
</Box>
</ContBox>
  );
}

export default Contacts
